import React from "react";
import "../../index.css";
const Header = (props) => {
	const dateReal = `${new Date().getHours()}:${new Date().getMinutes()}:${new Date().getSeconds()}`;
	const isOpen = props?.timeWork ? dateReal >= props?.timeWork[0]+12 && dateReal <= props?.timeWork[1]+12 ? true:false:false
	console.log(dateReal);
	console.log(props?.timeWork[0]+12);
