		      		)
		      	}

		      	{
		      		props?.timeWork && !isOpen && (
		      			<span className="inline-flex items-center justify-center px-2 py-1 text-xs font-bold leading-none text-white bg-red rounded">
		      				FECHADO AGORA
		      			</span>
		      		)	
		      	}
		    </div>
		</header>
	);
}
export default Header;
